                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       using UnityEngine;
using System.Collections;

public class TexFlow : MonoBehaviour {

    public float m_SpeedH = 0.1f;
	public float m_SpeedV = 0.1f;
	public Material mat;
    private Material tempMat;

    private Vector2 originalOffset;

    void Start()
    {
        mat = GetComponentInChildren<Renderer>().material;
        originalOffset = mat.mainTextureOffset;
    }

	// Update is called once per frame
	void Update ()
    {
        float newOffsetH = Time.time * m_SpeedH;
		float newOffsetV = Time.time * m_SpeedV;

        mat.mainTextureOffset = new Vector2(newOffsetH, newOffsetV);
	}

    void OnDestroy()
    {
        mat.mainTextureOffset = originalOffset;
    }

}